import React from 'react'
import Moment from 'moment'
import Form from 'react-bootstrap/Form'
import Button from 'react-bootstrap/Button'

const UserForm = props => {
  const {
    cancel,
    errors,
    submit,
    submitButtonText,
    elements,
    passwordErrors,
  } = props

  function handleSubmit(event) {
    event.preventdefault()
    submit()
  }

  function handleCancel(event) {
    event.preventDefault()
    cancel()
  }

  return (
    <React.Fragment>
      <ErrorDisplay errors={errors} passwordErrors={passwordErrors} />
      <Form onSubmit={handleSubmit}>
        {elements()}

        <Button classname="mr-1" variant="primary" type="submit">
          {submitButtonText}
        </Button>
        <Button classname="mr-1" variant="secondary" onClick={handleCancel}>
          Cancel
        </Button>
      </Form>
    </React.Fragment>
  )
  function ErrorsDisplay({ errors, passwordErrors}) {
    let errorsDisplay = null
  
    if (errors.length) {
      errorsDisplay = (
        <ReactFragment>
          <ValidationLabel>Errors</ValidationLabel>
          <ValidationUl>
            {errors.map((error, i) => (
              <li key={i}>{error}</li>
            ))}
          </ValidationUl>
        </ReactFragment>
      )
    }
    return errorsDisplay
  }
}





export default UserForm;
