import React from "react";
import Moment from "moment";

class Form extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      laaneBelop: null,
      nominellRente: null,
      terminGebyr: null,
      utlopsDato: null,
      saldoDato: null,
      datoForsteInnbetaling: null,
    };
  }
  mySubmitHandler = (event) => {
    event.preventDefault();
    alert("You are submitting " + this.state.laaneBelop);
    let jsonObject = {
      laanebelop: this.state.laaneBelop,
      nominellRente: this.state.nominellRente,
      terminGebyr: this.state.terminGebyr,
      utlopsDato: Moment(this.state.utlopsDato).format("YYYY-MM-DD"),
      saldoDato: Moment(this.state.saldoDato).format("YYYY-MM-DD"),
      datoForsteInnbetaling: Moment(this.state.datoForsteInnbetaling).format(
        "YYYY-MM-DD"
      ),
      ukjentVerdi: "TERMINBELOP",
    };
    this.props.handleSubmit(jsonObject);
  };
  myChangeHandler = (event) => {
    let nam = event.target.name;
    let val = event.target.value;
    this.setState({ [nam]: val });
  };
  render() {
    return (
      <form onSubmit={this.mySubmitHandler}>
        <h1>Generisk Banks lånekalkulator!</h1>
        <h5>
          Ta å fyll in skjemaet under, så vil tabellene og grafene oppdatere
          seg!
        </h5>
        <br></br>
        <div className="form-row">
          <div className="col">
            <div className="form-group">
              <label>Lånebeløp</label>
              <input
                type="number"
                className="form-control"
                name="laaneBelop"
                onChange={this.myChangeHandler}
                placeholder="Hvor mye ønsker du å låne?"
                required={true}
              />
            </div>
          </div>
          <div className="col">
            <div className="form-group">
              <label>Nominell rente</label>
              <input
                type="number"
                className="form-control"
                name="nominellRente"
                onChange={this.myChangeHandler}
                placeholder="Hvilken rente ønsker du?"
                required={true}
              />
            </div>
          </div>
          <div className="col">
            <label>Termingebyr</label>
            <input
              type="number"
              className="form-control"
              name="terminGebyr"
              onChange={this.myChangeHandler}
              placeholder="Hvor stort termingebyr ønsker du?"
              required={true}
            />
          </div>
        </div>
        <div className="form-row">
          <div className="col">
            <div className="form-group">
              <label>Hvor lang lånetid ønsker du?</label>
              <select class="form-control" id="exampleFormControlSelect1" onChange={this.myChangeHandler}>
                <option>5</option>
                <option>10</option>
                <option>15</option>
                <option>20</option>
                <option>25</option>
                <option>30</option>
              </select>
            </div>
          </div>
        </div>
        <div className="btn-group" role="group" aria-label="Basic example">
          <button className="btn btn-primary" type="submit">
            Kalkuler lån
          </button>
          <button className="btn btn-outline-danger btn-sm" type="reset">
            Avbryt
          </button>
        </div>
      </form>
    );
  }
}

export default Form;
